function Appointment(t){this.id=t.id,this.date=t.date,this.time=t.time,this.description=t.description,this.doctorId=t.doctor.id,this.patientId=t.patient.id,this.doctorName=t.doctor.name,this.patientName=t.patient.name,this.doctorSlug=t.doctor.slug,this.patientSlug=t.patient.slug}function setIntervalImmediately(t,e){return t(),setInterval(t,e)}function getAndAppendAppointments(){$.getJSON("/appointments").success(function(t){let e,n=[];for(e=0;e<t.length;e++){let i=new Appointment(t[e]);n.push(i)}$("#doctor-appointments-section").empty(),n.forEach(function(t){$("#doctor-appointments-section").append(t.returnHTML())})}).fail(function(t,e,n){const i="getJSON request failed! "+e+" - "+n;alert(i),console.log(i)})}Appointment.prototype.patientUrl=function(){return`/patients/${this.patientSlug}`},Appointment.prototype.returnHTML=function(){return`<div>\n              <table>\n                <tr><th>Date</th><td>${this.date}</td></tr>\n                <tr><th>Time</th><td>${this.time}</td></tr>\n                <tr><th>Patient</th><td><a href='${this.patientUrl()}'>${this.patientName}</a></td></tr>\n              </table>\n              <a href='/appointments/${this.id}' class='show-page-button'>View Full Details</a>\n              <hr>\n            </div>`},$(function(){$("#quick-create-form").on("submit",function(t){let e=this.action,n=$(this).serialize();$.post(e,n,function(){}).success($("#quick-create-form")[0].reset()).then($("#appointments-state-header").html("Today's Appointments")).then(getAndAppendAppointments()),t.preventDefault()}),setIntervalImmediately(function(){getAndAppendAppointments()},15e3)});